INSERT INTO `field_short_description` (`pages_id`, `data`) VALUES('1058', '<p><strong>Что нужно делать:</strong></p>\n\n<ul><li>координация деятельности сотрудников группы внутреннего контроля и аудита</li>\n	<li>участие в проведении плановых и внеплановых проверок ФХД филиалов, дочерних, зависимых обществах, отд</li>\n</ul><p><strong>Кого мы ищем:</strong></p>\n\n<ul><li>высшее образование (экономическое, финансовое),</li>\n	<li>знание нормативных правовых документов РФ,</li>\n	<li>опыт работы в области финансов, внутреннего контроля и аудита от 3 лет</li>\n</ul><p><strong>Что мы предлагаем:</strong></p>\n\n<ul><li>Оформление по ТК РФ,</li>\n	<li>Официальная заработная плата;</li>\n	<li>Оклад + премирование по результатам работы;</li>\n	<li>Пятидневная рабочая неделя с ненормированным рабочим днем;</li>\n	<li>Полный социальный пакет (ДМС, корпоративная сотовая связь, партнерские программы) Отличные возможности для карьерного роста и профессионального развития.</li>\n</ul>');
INSERT INTO `field_short_description` (`pages_id`, `data`) VALUES('1059', '<p><strong>Что нужно делать:</strong></p>\n\n<ul><li>координация деятельности сотрудников группы внутреннего контроля и аудита</li>\n	<li>участие в проведении плановых и внеплановых проверок ФХД филиалов, дочерних, зависимых обществах, отд</li>\n</ul><p><strong>Кого мы ищем:</strong></p>\n\n<ul><li>опыт работы в области финансов, внутреннего контроля и аудита от 3 лет</li>\n</ul><p><strong>Что мы предлагаем:</strong></p>\n\n<ul><li>Оформление по ТК РФ,</li>\n	<li>Официальная заработная плата;</li>\n</ul>');
